mapper.select:
    select

    from acct_refund_weixin
	where 1=1

          and id = #{po.id,jdbcType=INTEGER}


          and appid = #{po.appid,jdbcType=VARCHAR}


          and mch_id = #{po.mch_id,jdbcType=VARCHAR}


          and device_info = #{po.device_info,jdbcType=VARCHAR}


          and nonce_str = #{po.nonce_str,jdbcType=VARCHAR}


          and sign = #{po.sign,jdbcType=VARCHAR}


          and transaction_id = #{po.transaction_id,jdbcType=VARCHAR}


          and out_trade_no = #{po.out_trade_no,jdbcType=VARCHAR}


          and out_refund_no = #{po.out_refund_no,jdbcType=VARCHAR}


          and refund_id = #{po.refund_id,jdbcType=VARCHAR}


          and refund_channel = #{po.refund_channel,jdbcType=VARCHAR}


          and refund_fee = #{po.refund_fee,jdbcType=INTEGER}


          and total_fee = #{po.total_fee,jdbcType=INTEGER}


          and fee_type = #{po.fee_type,jdbcType=VARCHAR}


          and cash_fee = #{po.cash_fee,jdbcType=INTEGER}


          and cash_refund_fee = #{po.cash_refund_fee,jdbcType=INTEGER}


          and coupon_refund_fee = #{po.coupon_refund_fee,jdbcType=INTEGER}


          and coupon_refund_count = #{po.coupon_refund_count,jdbcType=INTEGER}


          and coupon_refund_id = #{po.coupon_refund_id,jdbcType=VARCHAR}
