mapper.select:
    select

    from room_price_billpkg
	where 1=1

          and id = #{po.id,jdbcType=INTEGER}


          and roomPriceId = #{po.roomPriceId,jdbcType=INTEGER}


          and billType = #{po.billType,jdbcType=INTEGER}


          and billDtlType = #{po.billDtlType,jdbcType=TINYINT}


          and amount = #{po.amount,jdbcType=DECIMAL}


          and basePrice = #{po.basePrice,jdbcType=DECIMAL}

